import React from 'react';

export const ThemeContext = React.createContext({
  theme: themes.dark,
  toggleTheme: () => {},
});


export const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
  },
};
